import chalk from 'chalk';

const loggingEnabled = process.env.ENABLE_AUTH_LOGGING === 'true';

if (!loggingEnabled) {
  console.log(`${chalk.bgWhite.bold('[AUTH]')} : Logs are disabled.`);
}

/**
 * If logs are enabled in the .env file - we log
 * We log in a way that the user knows that it's the auth module logging.
 * @param  {String} string what should be printed
 */
const prefixPrint = (string) => {
  if (loggingEnabled) {
    console.log(`${chalk.bgWhite.bold('[AUTH]')} ${string}`);
  }
};

export default {
  error: (message) => prefixPrint(chalk.bgRed(message)),
  warn: (message) => prefixPrint(chalk.bgRed(message)),
  success: (message) => prefixPrint(chalk.bgGreen(message)),
  info: (message) => prefixPrint(chalk.bgCyan(message)),
  plain: (message) => prefixPrint(message),
};
